import styled from "styled-components";
import {theme} from "../styles/Theme";

export  const Button = styled.button`
    margin-top: 5%;
    max-width: 240px;
    width: 100%;
    height: 50px;
    border: none;
    border-radius: 20px;
    background: linear-gradient(90.00deg, rgb(19, 176, 245),rgb(231, 15, 170));
    color: black;
    font-size: 1.1rem;
    font-weight:lighter;
    line-height: 26px;
    cursor: pointer;
&:hover{
    transform: scale(1.1);
    transition: ${theme.animation.transition};
}
`;
export const ButtonBorder = styled(Button)`
    position: relative;
    z-index: 1; /* Это нужно для правильного отображения текста поверх псевдоэлемента */

    &::before {
        content: '';
        position: absolute;
        top: -5px; /* Отступы для бордера */
        left: -5px;
        right: -5px;
        bottom: -5px;
        background: linear-gradient(45deg, ${theme.colors.secondColor}, ${theme.colors.primaryColor}); /* Градиент для бордера */
        border-radius: 20px; /* Совпадает с радиусом кнопки */
        z-index: -1; /* Псевдоэлемент будет за кнопкой */

    }

`;
