import styled from "styled-components";
import {theme} from "../styles/Theme";

export  const Button = styled.button`
    margin-top: 5%;
    max-width: 240px;
    width: 100%;
    height: 50px;
    border: none;
    border-radius: 20px;
    background: linear-gradient(90.00deg, rgb(19, 176, 245),rgb(231, 15, 170));
    color: black;
    font-size: 1.1rem;
    font-weight:lighter;
    line-height: 26px;
    cursor: pointer;
&:hover{
    transform: scale(1.1);
    transition: ${theme.animation.transition};
}
`;
export const ButtonBorder = styled(Button)`
    background-color: black; /* Черный фон */
    color: white; /* Белый текст */
    border: 5px solid transparent; /* Прозрачный бордер */
    position: relative;

    &::before {
        content: '';
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        background: linear-gradient(45deg, ${theme.colors.secondColor}, ${theme.colors.primaryColor}); /* Градиент для бордера */
        border-radius: 20px; /* Скругление углов бордера */
        z-index: -1; /* Псевдоэлемент должен быть за текстом */
        filter: blur(5px); /* Размытие градиентного бордера для мягкости */
        transform: scale(1.1); /* Немного увеличиваем градиент для эффекта "внешнего" бордера */
    }

    &:hover::before {
        filter: blur(8px); /* Размытие увеличивается на ховере */
    }
`;
