export const theme = {
    colors:{
        background:"black",
        secondBg:"rgb(54, 54, 54)",
        fontColor:"rgb(204, 204, 204)",
        primaryColor:"#bf3fa2",
        secondColor:"#07ade5"

    },
    media:{
        tablet:"screen and (max-width:768px)",
        mobile:"screen and (max-width:576px)",
        desktop:"screen and (min-width:1440px)",
    },
    animation:{
        transition: ".2s ease-in-out"
    }
}
